INSERT INTO `r_stata_tool_define` (`id`, `category_code`, `tool_code`, `tool_name`, `tool_desc`, `creator`, `create_time`, `last_modifier`, `last_modify_time`, `algorithm_file_r`, `method_signature`, `ret_type`, `params`, `version`, `run_count`, `del_flag`, `status`, `rel_article_id`, `tool_parent_code`, `rmd`, `orde`, `check_method_signature`, `worth_fishers`, `tool_type`, `include_r_files`) VALUES (40, '0101', 'Cox2', 'Cox回归多因素分析', '本模块应用Cox等比例风险模型实现生存资料的多因素分析，自动生成三线表格，无需修改可直接用于论文中，请下载word查看。<p style=\'text-indent: 2em;\'><a href=\'http://www.sci-inn.com/helps.html?id=2.1 \'  target=\'_blank\'>点击查看数据格式示例</a></p><p style=\'text-indent: 2em;\'>用到的R代码如下：</p><p style=\'text-indent: 4em;\'>#Cox回归分析</p><p style=\'text-indent: 4em;\'>library(survival) </p><p style=\'text-indent: 4em;\'>coxph(Surv(time, status) ~ var1 +var2, data=data1); </p>', 1, 1664443905, 1, 1664443905, 'Cox2.R', 'rt=Cox2(filename=filename,Surtime=Surtime,Surstatus=Surstatus,stratav=stratav,testc=testc,varselect=varselect,cateYN=cateYN,digitsn=digitsn,docFile=docfile)', 'REXP2HTML_DOCX', '{\"evals\":[\"testc\"],\"items\":[{\"datatype\":\"s\",\"desc\":\"生存时间\",\"name\":\"Surtime\",\"nullable\":\"n\"},{\"datatype\":\"s\",\"desc\":\"生存状态\",\"name\":\"Surstatus\",\"nullable\":\"n\"},{\"datatype\":\"s\",\"desc\":\"分层变量\",\"name\":\"stratav\",\"nullable\":\"n\"},{\"datatype\":\"s\",\"desc\":\"自变量\",\"name\":\"testc\",\"nullable\":\"n\"},{\"datatype\":\"s\",\"defaultV\":\"E\",\"desc\":\"变量选择方法\",\"name\":\"varselect\",\"nullable\":\"n\"},{\"datatype\":\"i\",\"defaultV\":\"2\",\"desc\":\"百分比的小数位数\",\"name\":\"digitsn\",\"nullable\":\"n\"},{\"datatype\":\"d\",\"defaultV\":\"5\",\"desc\":\"分类变量\",\"name\":\"cateYN\",\"nullable\":\"n\"}]}', 1, 6129, 'N', 2, NULL, NULL, NULL, 2, 'rt=check_Cox2(filename=filename,Surtime=Surtime,Surstatus=Surstatus,stratav=stratav,testc=testc,varselect=varselect,cateYN=cateYN,digitsn=digitsn,docFile=docfile)', 2, NULL, NULL);
INSERT INTO `r_stata_tool_define` (`id`, `category_code`, `tool_code`, `tool_name`, `tool_desc`, `creator`, `create_time`, `last_modifier`, `last_modify_time`, `algorithm_file_r`, `method_signature`, `ret_type`, `params`, `version`, `run_count`, `del_flag`, `status`, `rel_article_id`, `tool_parent_code`, `rmd`, `orde`, `check_method_signature`, `worth_fishers`, `tool_type`, `include_r_files`) VALUES (41, '0104', 'Maxstat_Cox1', 'Cox回归单因素及最大选择检验', '本模块应用Cox等比例风险模型实现生存资料的单因素分析，如果选择多个变量，则对多个变量分别进行单因素分析，而不是多因素分析。<p style=\'text-indent: 2em;\'>如果自变量是连续变量，可自动进行最大选择检验并选择最优界值，依据界值将连续变量转成二分类变量并进行Cox回归分析。</p><p style=\'text-indent: 2em;\'>如果自变量是多分类变量，由自动设置哑变量。（注：请设置“所有分类变量中最多的类别数”，如果一个变量的取值个数小于这个数，系统认为是分类变量，否则认为是连续变量。）</p><p style=\'text-indent: 2em;\'>分析结果自动生成三线表格，无需修改可直接用于论文中，请下载word查看。<p style=\'text-indent: 2em;\'><a href=\'http://www.sci-inn.com/helps.html?id=2.1 \'  target=\'_blank\'>点击查看数据格式示例</a></p><p style=\'text-indent: 2em;\'>用到的R代码如下：</p><p style=\'text-indent: 4em;\'>#Cox回归分析</p><p style=\'text-indent: 4em;\'>library(survival) </p><p style=\'text-indent: 4em;\'>coxph(Surv(time, status) ~ var1, data=data1); </p><p style=\'text-indent: 4em;\'>coxph(Surv(time, status) ~ as.factor(var1), data=data1); </p><br><p style=\'text-indent: 4em;\'>最大选择检验</p><p style=\'text-indent: 4em;\'>maxstat.test(Surv(time, status) ~ var1, data=data1, smethod=\"LogRank\",pmethod=\"Lau94\");</p><p style=\'text-indent: 4em;\'>cutoffvalue=maxtest$estimate;</p>', 1, 1615088383, 1, 1615088383, 'Maxstat_Cox1.R', 'rt=Maxstat_Cox1(filename=filename,Surtime=Surtime,Surstatus=Surstatus,testc=testc,cateYN=cateYN,digitsn=digitsn,docFile=docfile)', 'REXP2HTML_DOCX', '{\"evals\":[\"testc\"],\"items\":[{\"datatype\":\"s\",\"desc\":\"生存时间\",\"name\":\"Surtime\",\"nullable\":\"n\"},{\"datatype\":\"s\",\"desc\":\"生存状态\",\"name\":\"Surstatus\",\"nullable\":\"n\"},{\"datatype\":\"s\",\"desc\":\"自变量\",\"name\":\"testc\",\"nullable\":\"n\"},{\"datatype\":\"i\",\"defaultV\":\"2\",\"desc\":\"百分比的小数位数\",\"name\":\"digitsn\",\"nullable\":\"n\"},{\"datatype\":\"d\",\"defaultV\":\"5\",\"desc\":\"分类变量\",\"name\":\"cateYN\",\"nullable\":\"n\"}]}', 1, 4115, 'N', 2, NULL, '', NULL, 1, 'rt=check_Maxstat_Cox1(filename=filename,Surtime=Surtime,Surstatus=Surstatus,testc=testc,cateYN=cateYN,digitsn=digitsn,docFile=docfile)', 2, NULL, NULL);
INSERT INTO `r_stata_tool_define` (`id`, `category_code`, `tool_code`, `tool_name`, `tool_desc`, `creator`, `create_time`, `last_modifier`, `last_modify_time`, `algorithm_file_r`, `method_signature`, `ret_type`, `params`, `version`, `run_count`, `del_flag`, `status`, `rel_article_id`, `tool_parent_code`, `rmd`, `orde`, `check_method_signature`, `worth_fishers`, `tool_type`, `include_r_files`) VALUES (24, '0101', 'chisqtest1', '卡方检验', '本模块可实现对多组数据率（比例）的比较（二次数据最多支持4×4组），并根据样本量和最小期望值自动选择Pearson卡方检验、校正的Pearson卡方检验和Fisher精确检验。\r\n<p style=\'text-indent: 2em;\'>自动生成三线表格，无需修改可直接用于论文中。多组结果的两两比较不在网页上展示，请下载word查看。</p><p style=\'text-indent: 2em;\'><a href=\'http://www.sci-inn.com/helps.html?id=2.1 \'  target=\'_blank\'>点击查看数据格式示例</a></p><p style=\'text-indent: 2em;\'><a href=\'https://www.bilibili.com/video/BV1UT41197Vt/ \'  target=\'_blank\'>点击查看操作视频</a></p><p style=\'text-indent: 2em;\'>用到的R代码如下：</p><p style=\'text-indent: 4em;\'>#两组或多组的卡方检验 (样本量大于40且最小期望值大于5）</p><p style=\'text-indent: 4em;\'>chisq.test(table(variable1,variable2),correct = F) </p ><br><p style=\'text-indent: 4em;\'>#两组或多组的卡方检验 (样本量大于40且最小期望值小于5且大于等于1）</p><p style=\'text-indent: 4em;\'>chisq.test(table(variable1,variable2)) </p><br><p style=\'text-indent: 4em;\'>#两组或多组的卡方检验 (样本量小于40或最小期望值小于1）</p><p style=\'text-indent: 4em;\'>fisher.test(table(variable1,variable2)) </p>\r\n', 1, 1616331678, 1, 1616331678, 'chisqtest1.R', 'rt=chisqtest1(filename=filename,testvar=testvar,group=group,percent1=percent1,digits=digits,docFile=docfile)', 'REXP2HTML_DOCX', '{\"evals\":[],\"items\":[{\"datatype\":\"s\",\"desc\":\"检验变量\",\"name\":\"testvar\",\"nullable\":\"n\"},{\"datatype\":\"s\",\"desc\":\"分组变量\",\"name\":\"group\",\"nullable\":\"n\"},{\"datatype\":\"s\",\"defaultV\":\"c\",\"desc\":\"计算百分比\",\"name\":\"percent1\",\"nullable\":\"n\"},{\"datatype\":\"i\",\"defaultV\":\"2\",\"desc\":\"百分比的小数位数\",\"name\":\"digits\",\"nullable\":\"n\"}]}', 1, 11254, 'N', 2, NULL, 'chisqtest', NULL, 5, 'rt=check_chisqtest1(filename=filename,testvar=testvar,group=group,percent1=percent1,digits=digits,docFile=docfile)', 2, NULL, NULL);
INSERT INTO `r_stata_tool_define` (`id`, `category_code`, `tool_code`, `tool_name`, `tool_desc`, `creator`, `create_time`, `last_modifier`, `last_modify_time`, `algorithm_file_r`, `method_signature`, `ret_type`, `params`, `version`, `run_count`, `del_flag`, `status`, `rel_article_id`, `tool_parent_code`, `rmd`, `orde`, `check_method_signature`, `worth_fishers`, `tool_type`, `include_r_files`) VALUES (25, '0101', 'chisqtest2', '卡方检验', '两组或多组独立样本率的比较', 1, 1616331202, 1, 1616331202, 'chisqtest2.R', 'rt=chisqtest2(nr=nr,nc=nc,n11=n11,n12=n12,n13=n13,n14=n14,n21=n21,n22=n22,n23=n23,n24=n24,n31=n31,n32=n32,n33=n33,n34=n34,n41=n41,n42=n42,n43=n43,n44=n44,percent1=percent1,digits=digits,docFile=docfile)', 'REXP2HTML_DOCX', '{\"evals\":[],\"items\":[{\"datatype\":\"i\",\"desc\":\"四格表行数\",\"name\":\"nr\",\"nullable\":\"n\"},{\"datatype\":\"i\",\"desc\":\"四格表列数量\",\"name\":\"nc\",\"nullable\":\"n\"},{\"datatype\":\"s\",\"defaultV\":\"c\",\"desc\":\"计算百分比\",\"name\":\"percent1\",\"nullable\":\"n\"},{\"datatype\":\"i\",\"defaultV\":\"2\",\"desc\":\"百分比的小数位数\",\"name\":\"digits\",\"nullable\":\"n\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第1行，第1列输出的值\",\"name\":\"n11\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第1行，第2列输出的值\",\"name\":\"n12\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第1行，第3列输出的值\",\"name\":\"n13\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第1行，第4列输出的值\",\"name\":\"n14\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第2行，第1列输出的值\",\"name\":\"n21\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第2行，第2列输出的值\",\"name\":\"n22\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第2行，第3列输出的值\",\"name\":\"n23\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第2行，第4列输出的值\",\"name\":\"n24\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第3行，第1列输出的值\",\"name\":\"n31\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第3行，第2列输出的值\",\"name\":\"n32\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第3行，第3列输出的值\",\"name\":\"n33\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第3行，第4列输出的值\",\"name\":\"n34\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第4行，第1列输出的值\",\"name\":\"n41\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第4行，第2列输出的值\",\"name\":\"n42\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第4行，第3列输出的值\",\"name\":\"n43\",\"nullable\":\"y\"},{\"datatype\":\"i\",\"defaultV\":\"0\",\"desc\":\"第4行，第4列输出的值\",\"name\":\"n44\",\"nullable\":\"y\"}]}', 1, 6161, 'N', 3, NULL, 'chisqtest', NULL, NULL, 'rt=check_chisqtest2(nr=nr,nc=nc,n11=n11,n12=n12,n13=n13,n14=n14,n21=n21,n22=n22,n23=n23,n24=n24,n31=n31,n32=n32,n33=n33,n34=n34,n41=n41,n42=n42,n43=n43,n44=n44,percent1=percent1,digits=digits,docFile=docfile)', 0, NULL, NULL);
INSERT INTO `r_stata_tool_define` (`id`, `category_code`, `tool_code`, `tool_name`, `tool_desc`, `creator`, `create_time`, `last_modifier`, `last_modify_time`, `algorithm_file_r`, `method_signature`, `ret_type`, `params`, `version`, `run_count`, `del_flag`, `status`, `rel_article_id`, `tool_parent_code`, `rmd`, `orde`, `check_method_signature`, `worth_fishers`, `tool_type`, `include_r_files`) VALUES (64, '0101', 's1ttest1', '单样本t检验', '本模块可实现对一组数据的均值与固定值的比较，可用于原始数据和二次数据。<p style=\'text-indent: 2em;\'>自动生成三线表格，无需修改可直接用于论文中。</p><p style=\'text-indent: 2em;\'><a href=\'http://www.sci-inn.com/helps.html?id=2.1 \'  target=\'_blank\'>点击查看数据格式示例</a></p><p style=\'text-indent: 2em;\'>用到的R代码如下：</p><p style=\'text-indent: 4em;\'>#单样本t检验</p><p style=\'text-indent: 4em;\'> t.test(data1,mu=μ0)</p ><br><p style=\'text-indent: 4em;\'>#正态性检验（p<0.05时认为符合正态分布）</p><p style=\'text-indent: 4em;\'>shapiro.test(variable1)$p.value</p>', 1, 1615297782, 1, 1615297782, 's1ttest1.R', 'rt=s1ttest1(filename=filename,testvar=testvar,testvalue=testvalue,digits=digits,docFile=docfile)', 'REXP2HTML_DOCX', '{\"evals\":[],\"items\":[{\"datatype\":\"s\",\"desc\":\"检验变量\",\"name\":\"testvar\",\"nullable\":\"n\"},{\"datatype\":\"d\",\"defaultV\":\"0\",\"desc\":\"检验值\",\"name\":\"testvalue\",\"nullable\":\"n\"},{\"datatype\":\"i\",\"defaultV\":\"2\",\"desc\":\"小数位数\",\"name\":\"digits\",\"nullable\":\"n\"}]}', 1, 465, 'N', 2, NULL, 's1ttest', NULL, 7, 'rt=check_s1ttest1(filename=filename,testvar=testvar,testvalue=testvalue,digits=digits,docFile=docfile)', 2, NULL, NULL);
