INSERT INTO `r_stata_tool_define` (`id`, `category_code`, `tool_code`, `tool_name`, `tool_desc`, `creator`, `create_time`, `last_modifier`, `last_modify_time`, `algorithm_file_r`, `method_signature`, `ret_type`, `params`, `version`, `run_count`, `del_flag`, `status`, `rel_article_id`, `tool_parent_code`, `rmd`, `orde`, `check_method_signature`, `worth_fishers`, `tool_type`, `include_r_files`) VALUES (61, '0102', 'logistic2', '多因素二元Logistic回归分析', '本模块可实现多个变量的多因素二元Logistic回归分析，多分类变量可自动设置哑变量，可选择不同的变量筛选方法，自动生成三线表并对结果进行解读', 1, 1615027673, 1, 1615027673, 'logistic2.R', 'rt=logistic2(filename=filename,dependentvar=dependentvar,testc=testc,varselect=varselect,cateYN=cateYN,digitsn=digitsn,docFile=docfile)', 'REXP2HTML_DOCX', '{\"evals\":[\"testc\"],\"items\":[{\"datatype\":\"s\",\"desc\":\"因变量\",\"name\":\"dependentvar\",\"nullable\":\"n\"},{\"datatype\":\"s\",\"desc\":\"自变量\",\"name\":\"testc\",\"nullable\":\"n\"},{\"datatype\":\"s\",\"desc\":\"变量选择方法\",\"name\":\"varselect\",\"nullable\":\"n\"},{\"datatype\":\"d\",\"defaultV\":\"5\",\"desc\":\"分类变量\",\"name\":\"cateYN\",\"nullable\":\"n\"},{\"datatype\":\"i\",\"defaultV\":\"2\",\"desc\":\"百分比的小数位数\",\"name\":\"digitsn\",\"nullable\":\"n\"}]}', 1, 28055, 'N', 2, NULL, 'logistic', NULL, NULL, 'rt=check_logistic2(filename=filename,dependentvar=dependentvar,testc=testc,varselect=varselect,cateYN=cateYN,digitsn=digitsn,docFile=docfile)', 2, NULL, NULL);
